function getMappedErrorDescription(hl7FieldCode) {
    // hl7FieldCode = msg['MSA']['MSA.3']['MSA.3.1'].toString()
    var returnText = '';

    fieldCode = hl7FieldCode.replace(" is invalid.", "");
    fieldCode = fieldCode.replace(" is blank.", "");

    var fieldDescription = com.mirth.connect.model.hl7v2.Component.getSegmentorCompositeFieldDescription(fieldCode, false);

    if (fieldDescription && fieldDescription != "") {

        channelMap.put('fieldDescription', fieldDescription.toString());
        returnText = "Data for " + fieldDescription + " is blank or invalid";
        /*
        var Missing_Seg = com.mirth.connect.model.hl7v2.Component.getSegmentDescription(arr[0]);
        channelMap.put('Missing_Seg', Missing_Seg.toString());
        */
    } else {
        returnText = hl7FieldCode;
    }
    return returnText;
}



