    for (var reasonKey in APPLocationMap) {
        var reason = APPLocationMap[reasonKey];
        if (reason && parseInt(''+reason.IEN )== parseInt(''+ vistaIEN)) {
            return reason.EHRFieldId;
        }
    }
    return 0; // Default Value
}

