/**
    Get CPT Mapping for Vista and Corey

    @param {Any} coreyCode - 
    @return {Any} 
*/

function GetCPTForVistA(coreyCode) {
    var CPTMap = globalChannelMap.get('CPTMap');
    if (CPTMap == null) {
        CPTMap = JSON.parse(FileUtil.read("C:/MirthConfig/Mappings/CPTMapping.json"));
        logger.debug('CPT Map loaded successfully');
        globalChannelMap.put('CPTMap', CPTMap);
    }
    if (CPTMap == null) {
        logger.error("CPT Mapping is not available");
        return null;
    }
    for (var CPTKey in CPTMap) {
        var CPT = CPTMap[CPTKey];
        if (CPT && CPT.CPT == coreyCode) {
        	  var IEN=(CPT.IEN == undefined? "" : CPT.IEN)
            return IEN;
        }
    }
}

