/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function getDisposition(dispositionid) {

	var DispositionMap = globalChannelMap.get('DispositionMap');
    if (DispositionMap == null) {
       DispositionMap = JSON.parse(FileUtil.read("C:/MirthConfig/Mappings/DisPositionMapping.json"));
        logger.debug('Disposition Mapping loaded successfully');
        globalChannelMap.put('DispositionMap', DispositionMap);
    }
    if (DispositionMap == null) {
        logger.error("Disposition Mapping is not available");
        return null;
    }		

for (var DispositionKey in DispositionMap) {
	      var disposition = DispositionMap[DispositionKey];
	      if (disposition && disposition.num == dispositionid) {
	          return disposition.Disposition;
	      }
	  }
	  return '';
}

/**
	Function to execute HTTP Post Request

	@param {String} URL - web service url of corey server
	@param {String} request - Json 
	@return {String} returns the resultant json 
*/
function executePostRequest(URL, request){
	var object = Packages.com.vista.client.CMNVistAHttpClient;
	var coreyServer = object.cmnExecutePost(URL, request);
	var json = object.cmnGetJSON(coreyServer);
	return json;
}

/**
	Function to execute HTTP Get Request

	@param {String} URL - web service url of corey server
	@return {String} returns the resultant json 
*/
function executeGetRequest(URL){
	var object = Packages.com.vista.client.CMNVistAHttpClient;
	var ServerResponse = object.cmnExecuteGet(URL);
	var json = object.cmnGetJSON(ServerResponse);
	return json;
}

/**
	Methods & configurations to supports forms mappings
*/
function loadFormsConfiguration() {
    var configFilePath = 'C:/MirthConfig/VistAFormsConfig.json';
    var map = JSON.parse(ReadLocalFile(configFilePath));
    globalMap.put('VistAFormsConfig', map);
    return map;
}

function GetVistAForm(coreyViewName) {
    var map = globalMap.get('VistAFormsConfig') || loadFormsConfiguration();
    for (var key in map.Forms) {
        var masterItem = map.Forms[key];
        if (masterItem.CoreyName.toUpperCase() == coreyViewName.toUpperCase()) {
            return masterItem;
        }
    }
}
function GetCoreyForm(VistaName) {
    var map = globalMap.get('VistAFormsConfig') || loadFormsConfiguration();
    for (var key in map.Forms) {
        var masterItem = map.Forms[key];
        if (masterItem.VistaName.toUpperCase() == VistaName.toUpperCase()){// && masterItem.FormIEN == vistaFormIEN) {
            return masterItem;
        }
    }
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} baseURL - web service url of corey server
	@param {String} request - Json having the site details
	@return {String} return description Token generated from the authentication of Corey server
*/
function getCoreyAuthToken2222(baseURL, request){
	var object = Packages.com.vista.client.CMNVistAHttpClient;
	var url = baseURL + "/RegisterDeviceid_s.aspx";
	var auth = object.cmnExecutePost(url, request);
	var decrypt = decryptAES(auth,baseURL);
	var json = object.cmnGetJSON(decrypt);
	var parsedJSON = JSON.parse(json);
	return parsedJSON['token'];
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
var DBDriverName = configurationMap.get('DBDriverName');
var DBDSNName = configurationMap.get('DBDSNName');
var DBUserID = configurationMap.get('DBUserName');
var DBPassword = configurationMap.get('DBPassword');
var DBCon = "null";



//function to make Database Connection
function OpenDBCon()
{
	try
	{
		return DBCon = DatabaseConnectionFactory.createDatabaseConnection(DBDriverName,DBDSNName,DBUserID,DBPassword); 
	} catch(err)
	{
		logger.error("Database Is Down At - "+ DateUtil.getCurrentDate("yyyyMMddHHmmss"));
		return false;
	}
}

/**
	CloseDBCon

	@param {String} DBCon
*/
function CloseDBCon() //DB Connection Close
{
	try
	{
		DBCon.close();
	} catch(err)
	{
		logger.error("Exception In Closing Database - "+ DateUtil.getCurrentDate("yyyyMMddHHmmss"));
	}
}

/**
	executeSelectQuery

	@param {String} SQLString
	@param {String} DBConn
	@return ResultSet
*/
function executeSelectQuery(SQLString){ //select query execution for fetching the data from DB
	try {
		DBConn = OpenDBCon();
		var ResultSet = DBConn.executeCachedQuery(SQLString);
	} catch(err){
		logger.error("Error occurred while executing select statement "+ SQLString + " At - "+ DateUtil.getCurrentDate("yyyyMMddHHmmss"));
	} finally {
		if (DBConn) {
			CloseDBCon();
		}
	}
	return ResultSet;
}

function getVistAPatientID(coreyPatientID,Org_Name) {  // function for getting the vista pat id from DB using corey pat ID
	var SQLString = "SELECT [EXTSystemPatientID]  AS VistaPatientID, [ID] AS FK_PATIENTID FROM [CMNPatientMapping] WHERE [CoreyPatientID] = '"+ coreyPatientID +"' and [EXTSystem] = 'Vista'" + " AND Org_Name = '" + Org_Name + "'";
	channelMap.put("SELECTSQL", SQLString) // changed [VistaPatientID] to [EXTSystemPatientID]
	var resultSet = executeSelectQuery(SQLString);  
	while(resultSet.next()){
		var VistaPtID = resultSet.getString("VistaPatientID");
		fkPatientId = resultSet.getString("FK_PATIENTID");
		channelMap.put("VistaPatientID", VistaPtID);

		}
		return (VistaPtID && VistaPtID != 'null') ? VistaPtID : "";	
}
function getPatientID(patientID, Message_Control_Id,Org_Name) {
    var coreyPatId = ""
    if (patientID) {
        var SQLString = "Select [CoreyPatientID] AS CoreyPatientID from CMNPatientMapping where [EXTSystemPatientID] = '" + patientID + "'" + " AND Org_Name = '" + Org_Name + "'"; // changed [VistaPatientID] to [EXTSystemPatientID] 
        channelMap.put("getPatientID_CMNPatientMapping", SQLString);
        //coreyPatId = executeScalar(SQLString);

        var resultSet = executeSelectQuery(SQLString);  
        while(resultSet.next()){
		coreyPatId = resultSet.getString("CoreyPatientID");
		channelMap.put("coreyPatId", coreyPatId);

		}

    }

    if ((coreyPatId == null || typeof coreyPatId == 'undefined' || coreyPatId.trim().length <= 0) && Message_Control_Id) {
        var SQLString = "SELECT [Patient_Id] as CoreyPatientID FROM CMN_Corey_Org WHERE [Message_Control_Id] = '" + Message_Control_Id + "'" + " AND Org_Name = '" + Org_Name + "'"; // changed [VistaPatientID] to [EXTSystemPatientID] 
        channelMap.put("getPatientID_CMNCoreyOrg", SQLString);
         var resultSet = executeSelectQuery(SQLString);  
        while(resultSet.next()){
		coreyPatId = resultSet.getString("CoreyPatientID");
		channelMap.put("coreyPatId", coreyPatId);

		}


    }
	  if ((coreyPatId == null || typeof coreyPatId == 'undefined' || coreyPatId.trim().length <= 0)) {
	  	coreyPatId="0000";
	  }
    return coreyPatId;
}
