function GetCoreySpecialty(organizationName, vistAValue) {
	var VistASpecialtyMap = globalMap.get(organizationName + '_VistASpecialtyMap');
	//VistASpecialtyMap = GetSpecialtyMappingFromDB(organizationName);
	//globalMap.put(organizationName + '_VistASpecialtyMap', VistASpecialtyMap);
	//if(VistASpecialtyMap == null)
     if(VistASpecialtyMap == null || (VistASpecialtyMap == 'undefined')){
		VistASpecialtyMap = GetSpecialtyMappingFromDB(organizationName);
		//globalMap.put(organizationName + '_VistASpecialtyMap', VistASpecialtyMap);
		globalMap.put(organizationName + '_VistASpecialtyMap', VistASpecialtyMap);
	}
	for (var providerKey in VistASpecialtyMap) {
	        var specialty = VistASpecialtyMap[providerKey];
	        //if (specialty && specialty.Org_Name == organizationName && specialty.Ext_SubSpecialtyIEN == vistAValue) 
	        if (specialty && (specialty.Ext_SubSpecialtyIEN == vistAValue)){
	            return [specialty.Corey_SpecialtyId,specialty.Corey_SubSpecialtyId];	           
	        }
	    }
    return null;
}

function GetSpecialtyMappingFromDB(orgName) {
	var DBDriverName = configurationMap.get('DBDriverName');
	var DBDSNName = configurationMap.get('DBDSNName');
	var DBUserID = configurationMap.get('DBUserName');
	var DBPassword = configurationMap.get('DBPassword');
	try {
		//var SQLString = "SELECT [Corey_SpecialtyId] ,[Corey_SubSpecialtyId],[Ext_SubSpecialtyIEN], [Org_Name] FROM [SpecialtyMapping] WHERE  Org_Name='"+Org_Name+"'";

		var specialtyMappingSQL = "SELECT * FROM [CoreyMappingDEV].[dbo].[SpecialtyMapping]";
		var SpecialtyMappingArray = [];
	    DBConn = DatabaseConnectionFactory.createDatabaseConnection(DBDriverName, DBDSNName, DBUserID, DBPassword);
	    try {
	        var results = DBConn.executeCachedQuery(specialtyMappingSQL);
	        var resultSize = results.size();
	        if (resultSize > 0) {
	            for (var i = 0; i < resultSize; i++) {
	                results.next();
	                SpecialtyMappingArray.push(
	                	{
	                		"Corey_SpecialtyId": results.getString(2),
	                		"Corey_SpecialtyName": results.getString(3),
	                		"Corey_SubSpecialtyId": results.getString(4),
	                		"Corey_SubSpecialtyName": results.getString(5),
	                		"Ext_SubSpecialtyIEN": results.getString(6),
	                		"Ext_SubSpecialtyName": results.getString(7),
	                		"Ext_SystemName": results.getString(8),
	                		"TAXONOMY_Code": results.getString(9),
	                		"Org_Name": results.getString(10)

	                	}
	                );
	            }
	        }
	    } catch (err) {
	        logger.error("Error occurred while executing select statement " + specialtyMappingSQL);
	    } finally {
	        if (DBConn) {
	            DBConn.close();
	        }
	    }

	} catch (err) {
	    logger.error("Database Is Down At - " + specialtyMappingSQL);
	    return false;
	}
	return SpecialtyMappingArray;
}

