function GetAPPLocationForVistA(coreyCode) {
	var APPLocationMap = globalChannelMap.get('APPLocationMap');
    if (APPLocationMap == null) { 
        APPLocationMap = JSON.parse(FileUtil.read("C:/MirthConfig/Mappings/AppointmentLocation.json"));
        logger.debug('APPLocation Map loaded successfully');
        globalChannelMap.put('APPLocationMap', APPLocationMap);
    }
    if (APPLocationMap == null) {
        logger.error("APPLocation Mapping is not available");
        return null;
    }

    for (var reasonKey in APPLocationMap) {
        var reason = APPLocationMap[reasonKey];

        if (reason && parseInt(''+ reason.EHRFieldId) == parseInt(''+ coreyCode)) {
            return new String(reason.IEN);
        }
    }
    return ''; // Default Value
}

function GetAPPLocationForCorey(vistaIEN) {
	var APPLocationMap = globalChannelMap.get('APPLocationMap');
    if (APPLocationMap == null) { 
        APPLocationMap = JSON.parse(FileUtil.read("C:/MirthConfig/Mappings/AppointmentLocation.json"));
        logger.debug('APPLocation Map loaded successfully');
        globalChannelMap.put('APPLocationMap', APPLocationMap);
    }
    if (APPLocationMap == null) {
        logger.error("APPLocation Mapping is not available");
        return null;
    }
    for (var reasonKey in APPLocationMap) {
        var reason = APPLocationMap[reasonKey];
        if (reason && parseInt(''+reason.IEN )== parseInt(''+ vistaIEN)) {
            return reason.EHRFieldId;
        }
    }
    return 0; // Default Value
}

function getschcancelmap() {
    var SCHCancellationReasonMap = globalChannelMap.get('SCHCancellationReasonMap');
    if (SCHCancellationReasonMap == null) {
        SCHCancellationReasonMap = JSON.parse(FileUtil.read("C:/MirthConfig/Mappings/SCH_CancellationReasonMap.json"));
        logger.debug('SCHCancellationReason Mapping loaded successfully');
        globalChannelMap.put('SCHCancellationReasonMap', SCHCancellationReasonMap);
    }
    if (SCHCancellationReasonMap == null) {
        logger.error("SCHCancellationReason Mapping is not available");
        return null;
    }
}
function GetSCHCancellationReasonForVistA(coreyCode) {
	getschcancelmap();
    for (var reasonKey in SCHCancellationReasonMap) {
        var reason = SCHCancellationReasonMap[reasonKey];

        if (reason && parseInt(''+ reason.EHRFieldId) == parseInt(''+ coreyCode)) {
            return new String(reason.IEN);
        }
    }
    return ''; // Default Value
}

function GetSCHCancellationReasonForCorey(vistaIEN) {
	getschcancelmap();
    for (var reasonKey in SCHCancellationReasonMap) {
        var reason = SCHCancellationReasonMap[reasonKey];
        if (reason && reason.IEN == vistaIEN) {
            return reason.EHRFieldId;
        }
    }
    return 0; // Default Value
}

function getapptcancelmap() {
    var ApptCancellationReasonMap = globalChannelMap.get('ApptCancellationReasonMap');
    if (ApptCancellationReasonMap == null) {
        ApptCancellationReasonMap = JSON.parse(FileUtil.read("C:/MirthConfig/Mappings/Appt_CancellationReasonMap.json"));
        logger.debug('ApptCancellationReason Mapping loaded successfully');
        globalChannelMap.put('ApptCancellationReasonMap', ApptCancellationReasonMap);
    }
    if (ApptCancellationReasonMap == null) {
        logger.error("ApptCancellationReason Mapping is not available");
        return null;
    }
}
function GetApptCancellationReasonForVistA(coreyCode) {
	getapptcancelmap();
    for (var reasonKey in ApptCancellationReasonMap) {
        var reason = ApptCancellationReasonMap[reasonKey];
        if (reason && reason.EHRFieldId == coreyCode) {
            return reason.IEN;
        }
    }
    return ""; // Default Value
}

function GetApptCancellationReasonForCorey(vistaIEN) {
	getapptcancelmap();
    for (var reasonKey in ApptCancellationReasonMap) {
        var reason = ApptCancellationReasonMap[reasonKey];
        if (reason && reason.IEN == vistaIEN) {
            return reason.EHRFieldId;
        }
    }
    return 0; // Default Value
}

/**
    Get CPT Mapping for Vista and Corey

    @param {Any} coreyCode - 
    @return {Any} 
*/

function GetCPTForVistA(coreyCode) {
    var CPTMap = globalChannelMap.get('CPTMap');
    if (CPTMap == null) {
        CPTMap = JSON.parse(FileUtil.read("C:/MirthConfig/Mappings/CPTMapping.json"));
        logger.debug('CPT Map loaded successfully');
        globalChannelMap.put('CPTMap', CPTMap);
    }
    if (CPTMap == null) {
        logger.error("CPT Mapping is not available");
        return null;
    }
    for (var CPTKey in CPTMap) {
        var CPT = CPTMap[CPTKey];
        if (CPT && CPT.CPT == coreyCode) {
        	  var IEN=(CPT.IEN == undefined? "" : CPT.IEN)
            return IEN;
        }
    }
}

function GetCPTForCorey(vistaIEN) {
    var CPTMap = globalChannelMap.get('CPTMap');
    if (CPTMap == null) {
        CPTMap = JSON.parse(FileUtil.read("C:/MirthConfig/Mappings/CPTMapping.json"));
        logger.debug('CPT Map loaded successfully');
        globalChannelMap.put('CPTMap', CPTMap);
    }
    if (CPTMap == null) {
        logger.error("CPT Mapping is not available");
        return null;
    }
    for (var CPTKey in CPTMap) {
        var CPT = CPTMap[CPTKey];
        if (CPT && CPT.IEN == vistaIEN) {
        	 var  CPT=(CPT.CPT == undefined? "" : CPT.CPT)
            return CPT;
        }
    }
}

