function getTIUINFO(vname) {
	var TIUMAPPING = globalChannelMap.get('TIUMAPPING');
    if (TIUMAPPING == null) {
        TIUMAPPING = JSON.parse(FileUtil.read("C:/MirthConfig/Mappings/TIU_InfoMapping.json"));
        logger.debug('TIUMAPPING loaded successfully');
        globalChannelMap.put('TIUMAPPING', TIUMAPPING);
    }
    if (TIUMAPPING == null) {
        logger.error("TIUMAPPING is not available");
        return null;
    }			
	for (var TIUKey in TIUMAPPING) {
	      var TIUINFO = TIUMAPPING[TIUKey];
	      if (TIUINFO && (TIUINFO.VIEWNAME == vname)){
	          return TIUINFO.VISTAIEN +"^"+TIUINFO.TITLE;
	      }
	     }
	     return ""
}

