//Get corey Appointment id from DB using vista Case id
function getCoreyAppID(vistACaseID) {
    SQLString = "SELECT [CoreyApptID] FROM [dbo].[CMNApptMapping] WHERE [VistaCaseID] = '" + vistACaseID + "'" + " AND Org_Name = '" + channelMap.get('orgname') + "'";
    channelMap.put("SELECTSQL", SQLString);
    var resultSet = executeSelectQuery(SQLString);
    while (resultSet.next()) {
