function GetCoreySpecialty(organizationName, vistAValue) {
	var VistASpecialtyMap = globalMap.get(organizationName + '_VistASpecialtyMap');
	//VistASpecialtyMap = GetSpecialtyMappingFromDB(organizationName);
	//globalMap.put(organizationName + '_VistASpecialtyMap', VistASpecialtyMap);
