function gettokenName(msgControlId) {
    SQLString = "SELECT [Token] FROM [dbo].[CMN_Corey_Org] WHERE [Message_Control_Id] = '" + msgControlId + "'" + " AND Org_Name = '" + channelMap.get('orgname') + "'";
      channelMap.put("SELECTSQL" + msgControlId, SQLString);
    var resultSet = executeSelectQuery(SQLString);
    while (resultSet.next()) {
        var token = resultSet.getString("TOKEN");
        channelMap.put("VIEW_NAME", token);
    }
    return token;
}

/**
	Function for Anonymizing the patient Name.

	@param {String} patientName - Value of patient name
	@param {String} nameType - Name format 'last,first' or 'last'
	@return {String} return anonymizedName 
*/
function getAnonymizeName (patientName, nameType) {
	var anonymizedName = "";
	if(nameType == 'last,first'){
		var name = patientName.split(',');
		anonymizedName = name[0].substring(0,3);
		anonymizedName = anonymizedName + ',' + name[1].substring(0,1);
	} else if (nameType == 'last') {
		anonymizedName = patientName.substring(0,3);
	} else {
		anonymizedName = patientName.substring(0,1);
	}
	channelMap.put("anonymizedName",anonymizedName);
	return anonymizedName;
}

