        globalChannelMap.put('problemMap', problemMap);
    }
    if (problemMap == null) {
        logger.error("problemMap is not available");
        return null;
    }		
		for (var problemKey in problemMap) {
	      var problem = problemMap[problemKey];
	      if (problem && problem.IEN == ien) {
	          return problem;
	      }
	  }
	  return '';
}

