        var jsonObj = JSON.parse(serverJson);
        var code = jsonObj['Result']['Code'];
        return code;
    }

    var token = globalMap.get('RHJVAMC-authToken'); // get Auth token
    channelMap.put('token',token);

    while (coreySendCounter < 5){    
        channelMap.put('coreySendCounter'+coreySendCounter,coreySendCounter);
        var coreyServerResponse = sendCoreyResponse(token);
        if (coreyServerResponse == 1000) { //if token is invalid, generate new token.
            token = router.routeMessage('VHACHY-Refresh Corey Server Token', '{"test":"test"}');
            token = token.getMessage();                    
        } else {
            break;
        }
        coreySendCounter++;
    }

}

