function getLabOrder(getLabOrder) {
    //SQLString = "SELECT [CoreyApptID] FROM [dbo].[CMNOrderAppointment] WHERE [VistaCaseID] = '" + vistACaseID + "'";
    SQLString = "SELECT cast([CoreyApptID] AS varchar(25)) CoreyApptID,OrderUniqueId FROM [dbo].[CMNOrderAppointment] WHERE [VistaCaseID] = '" + getLabOrder + "'" + " AND Org_Name = '" + channelMap.get('orgname') + "'";
    channelMap.put("SELECTSQLPonnu", SQLString);
    var resultSet = executeSelectQuery(SQLString);
    logger.info("test" - resultSet);
    while (resultSet.next()) {
        var schId = resultSet.getString("CoreyApptID");
        var orderUniqueid = resultSet.getString("OrderUniqueId");
        channelMap.put("CoreyApptID", schId);
        channelMap.put("schId", orderUniqueid);
    }
    return [schId, orderUniqueid];
}

