function getCoreySpecID(vistASpecID) {
    SQLString = "SELECT [Corey_SpecialtyId],[Corey_SubSpecialtyId] FROM [dbo].[SpecialtyMapping] WHERE [Ext_SubSpecialtyIEN] = '" + vistASpecID + "'" + " AND Org_Name = '" + channelMap.get('orgname') + "'";
