    while (resultSet.next()) {
        var coreySpecID = resultSet.getString("Corey_SpecialtyId");
        var coreysubSpecID = resultSet.getString("Corey_SubSpecialtyId");
        channelMap.put("COREYSPECID", coreySpecID);
        channelMap.put("COREYSUBSPECID", coreysubSpecID);
    }
    return [coreySpecID, coreysubSpecID];
}

function getOrganizationName(control, uselike) {
    if (uselike) {
        SQLString = "SELECT [Org_Name] FROM [dbo].[CMN_Corey_Org] WHERE [Message_Control_Id] LIKE '%" + control + "%';";
    } else {
        SQLString = "SELECT [Org_Name] FROM [dbo].[CMN_Corey_Org] WHERE [Message_Control_Id] = '" + control + "';";
    }

    channelMap.put("getOrganizationNameSQL", SQLString);
    var resultSet = executeSelectQuery(SQLString);
    while (resultSet.next()) {
        var Org_Name = resultSet.getString("Org_Name");
    }
    return Org_Name;
}

function deleteFailedAppointmentMapping(msgControlId) {
    var SQLString = "DELETE FROM [dbo].[CMNApptMapping] WHERE [VistaCaseID] IS NULL AND [MsgControlID] = '" + msgControlId + "'" + " AND Org_Name = '" + channelMap.get('orgname') + "'";
    channelMap.put("deleteFailedAppointmentMapping", SQLString);
    var resultSet = executeInsertQuery(SQLString);
}

