function GetHospitalLocation(timevalue) {
	var hLoc="";
     if (timevalue <=0800) {
		hLoc= "32"
     } else if (timevalue <=1600) {
		hLoc="33"
     } else {
		hLoc="34"
     }
     return hLoc;
}

function gethospitallocmap() {
    var HOSPITALLOCATIONMAPPING = globalChannelMap.get('HOSPITALLOCATIONMAPPING');
    if (HOSPITALLOCATIONMAPPING == null) {
        HOSPITALLOCATIONMAPPING = JSON.parse(FileUtil.read("C:/MirthConfig/Mappings/Hospital_LocationMapping.json"));
        logger.debug('HOSPITALLOCATION Mapping loaded successfully');
        globalChannelMap.put('HOSPITALLOCATIONMAPPING', APPLocationMap);
    }
    if (HOSPITALLOCATIONMAPPING == null) {
        logger.error("HOSPITALLOCATION Mapping is not available");
        return null;
    }
}

function GetHospitalLocationForVistA(coreyCode) {
	gethospitallocmap();
    for (var reasonKey in HOSPITALLOCATIONMAPPING) {
        var reason = HOSPITALLOCATIONMAPPING[reasonKey];
        if (reason && reason.EHRFieldId == coreyCode) {
            return reason.location;
        }
    }
    return ""; // Default Value
}


function GetHospitalEDLocationForVistA(locName) {

	var HOSPITALEDLOCATIONMAPPING = globalChannelMap.get('HOSPITALEDLOCATIONMAPPING');
    if (HOSPITALEDLOCATIONMAPPING == null) {
        HOSPITALEDLOCATIONMAPPING = JSON.parse(FileUtil.read("C:/MirthConfig/Mappings/HospitalEDLocMapping.json"));
        logger.debug('HOSPITALEDLOCATION Mapping loaded successfully');
        globalChannelMap.put('HOSPITALEDLOCATIONMAPPING', HOSPITALEDLOCATIONMAPPING);
    }
    if (HOSPITALEDLOCATIONMAPPING == null) {
        logger.error("HOSPITALEDLOCATIONMAPPING is not available");
        return null;
    }		

    for (var reasonKey in HOSPITALEDLOCATIONMAPPING) {
        var reason = HOSPITALEDLOCATIONMAPPING[reasonKey];
        if (reason && reason.location == locName) {
            return reason.EHRFieldId;
        }
    }
    return ""; // Default Value
}

