/**
	Get Immunization Dosage Unit ForVistA
*/
function GetImmunizationDosageUnitForVistA(strCoreyCode) {
	var unitMap = globalChannelMap.get('unitMap');
    if (unitMap == null) {
       unitMap = JSON.parse(FileUtil.read("C:/MirthConfig/Mappings/DosageUnitMapping.json"));
        logger.debug('unit Mapping loaded successfully');
        globalChannelMap.put('unitMap', unitMap);
    }
    if (unitMap == null) {
        logger.error("unit Mapping is not available");
        return null;
    }		
		for (var unitKey in unitMap) {
	      var unit = unitMap[unitKey];
	      if (unit && unit.CoreyCode == strCoreyCode) {
	          return unit.IEN;
	      }
	  }
	  return '';
}

