function GetOftenForVistA(strCoreyCode) { 
	var OftenMap = globalChannelMap.get('OftenMap');
    if (OftenMap == null) {
        OftenMap = JSON.parse(FileUtil.read("C:/MirthConfig/Mappings/Lab_OftenMapping.json"));
        logger.debug('Often Mapping loaded successfully');
        globalChannelMap.put('OftenMap', OftenMap);
    }
    if (OftenMap == null) {
        logger.error("Often Mapping is not available");
        return null;
    }   
		for (var OftenKey in OftenMap) {
	      var Often = OftenMap[OftenKey];
	      globalChannelMap.put('OftenMap', OftenMap);
	      if (Often && Often.IEN == strCoreyCode) {
	          return Often.Often;
	      }
	  }
	  return '';
}
function GetOftenForCorey(description) {
	var OftenMap = globalChannelMap.get('OftenMap');
    if (OftenMap == null) {
