/**
	Get VistA - Corey Provider Mapping
*/
function GetVistAProviderId(organizationName, coreyProviderId) {
	var vistaProviderMap = globalMap.get('VistAProviderMap');
	//vistaProviderMap==null
	 //vistaProviderMap = ReadCoreyVistAProviderMapping();
	 //globalMap.put('VistAProviderMap', vistaProviderMap);
	//vistaProviderMap = ReadCoreyVistAProviderMapping();
	if(vistaProviderMap == null){
		//logger.info("ReadCoreyVistAProviderMapping");
		vistaProviderMap = ReadCoreyVistAProviderMapping();
		globalMap.put('VistAProviderMap', vistaProviderMap);
	}

	for (var providerKey in vistaProviderMap) {
	        var provider = vistaProviderMap[providerKey];
	  		channelMap.put("prov",organizationName);
	        if ((provider && provider.Org_Name.toUpperCase() == organizationName.toUpperCase()) && (provider.CoreyProviderId == coreyProviderId)) {
	            return provider.VistaProviderId;

	        }

	    }
	    if (provider.CoreyProviderId == "") {
	        	var SQLString = "SELECT [VistaProviderId] FROM [CMN_Vista_ProviderMapping] WHERE [IsActive] = 1 and Org_Name='"+organizationName+"' and CoreyProviderId="+ coreyProviderId;
	        	 VistaProviderId = executeScalar(SQLString);	        	
	        	if (VistaProviderId != "null" && (VistaProviderId!="")) {
	        		return VistaProviderId;
	        		} else {
	        			return ""
	        			}
	        	}
   return ""
}

function GetCoreyProviderId(organizationName, vistAProviderId) {
	var vistaProviderMap = globalMap.get('VistAProviderMap');
	if(vistaProviderMap == null){
		vistaProviderMap = ReadCoreyVistAProviderMapping();
		globalMap.put('VistAProviderMap', vistaProviderMap);
	}

	for (var providerKey in vistaProviderMap) {
	        var provider = vistaProviderMap[providerKey];
	        if (provider && provider.Org_Name == organizationName && provider.VistaProviderId == vistAProviderId) {
	        	  return provider.CoreyProviderId;
	        }
	    }

    var DBConn = "null";
    var SQLString = "SELECT [CoreyProviderId]  FROM [CMN_Vista_ProviderMapping] WHERE [IsActive] = 1 and Org_Name='"+organizationName+"' and VistaProviderId="+ vistAProviderId ;
    logger.error(SQLString);

    try {
    var DBDriverName = configurationMap.get('DBDriverName');
    var DBDSNName = configurationMap.get('DBDSNName');
    var DBUserID = configurationMap.get('DBUserName');
    var DBPassword = configurationMap.get('DBPassword');
        DBConn = DatabaseConnectionFactory.createDatabaseConnection(DBDriverName, DBDSNName, DBUserID, DBPassword);
        try {
            var resultSet = DBConn.executeCachedQuery(SQLString);

	if (resultSet) {
		while (resultSet.next()) {
			var CoreyProviderId = resultSet.getString("CoreyProviderId");
			 return CoreyProviderId;
			//logger.info(VistaPtID);
		}
	} else {
		channelMap.put('Missing Mapping Provider mapping not available');
	}

        }
     catch (err) {
            logger.error("Error occurred while executing select statement ");
        } finally {
            if (DBConn) {
                DBConn.close();
            }
        }
    } catch (err) {
        logger.error("Database Is Down At - " + DateUtil.getCurrentDate("yyyyMMddHHmmss"));
        return null;
    }

    return null;
}



function ReadCoreyVistAProviderMapping() {
    var DBDriverName = configurationMap.get('DBDriverName');
    var DBDSNName = configurationMap.get('DBDSNName');
    var DBUserID = configurationMap.get('DBUserName');
    var DBPassword = configurationMap.get('DBPassword');

    var DBConn = "null";
    var SQLString = "SELECT [CoreyProviderId] ,[VistaProviderId], [Org_Name] FROM [CMN_Vista_ProviderMapping] WHERE [IsActive] = 1 ";

    var providerMap = [];

    try {
        DBConn = DatabaseConnectionFactory.createDatabaseConnection(DBDriverName, DBDSNName, DBUserID, DBPassword);
        try {
            var results = DBConn.executeCachedQuery(SQLString);
            var resultSize = results.size();
            if (resultSize > 0) {
            	logger.info("resultSize"+ resultSize);
                for (var i = 0; i < resultSize; i++) {
                    results.next();
                    providerMap.push({
                        "CoreyProviderId": results.getString(1),
                        "VistaProviderId": results.getString(2) != null ? results.getString(2) : '',
                        "Org_Name": results.getString(3)
                    });
                }
            }
        } catch (err) {
            logger.error("Error occurred while executing select statement " + SQLString + " At - " + DateUtil.getCurrentDate("yyyyMMddHHmmss"));
        } finally {
            if (DBConn) {
                DBConn.close();
            }
        }

    } catch (err) {
        logger.error("Database Is Down At - " + DateUtil.getCurrentDate("yyyyMMddHHmmss"));
        return null;
    }
    return providerMap;
}


