//Database Connection Variables
/*
var DBDriverName = "net.sourceforge.jtds.jdbc.Driver";
var DBDSNName = "jdbc:jtds:sqlserver://localhost:1433/CoreyMapping";
var DBUserID = "sa";
var DBPassword = "dev@123";
*/
var DBDriverName = configurationMap.get('DBDriverName');
var DBDSNName = configurationMap.get('DBDSNName');
var DBUserID = configurationMap.get('DBUserName');
var DBPassword = configurationMap.get('DBPassword');

var DBCon = "null";

//function to make Database Connection
function OpenDBCon() {
    try {
        return DBCon = DatabaseConnectionFactory.createDatabaseConnection(DBDriverName, DBDSNName, DBUserID, DBPassword);
    } catch (err) {
        logger.error("Database Is Down At - " + DateUtil.getCurrentDate("yyyyMMddHHmmss"));
        return false;
    }
}

/**
    CloseDBCon

    @param {String} DBCon
*/
function CloseDBCon() {
    try {
        DBCon.close();
    } catch (err) {
        logger.error("Exception In Closing Database - " + DateUtil.getCurrentDate("yyyyMMddHHmmss"));
    }
}

/**
    executeSelectQuery

    @param {String} SQLString
    @param {String} DBConn
    @return ResultSet
*/
function executeSelectQuery(SQLString) { //select query execution for fetching the data from DB
    try {
        DBConn = OpenDBCon();
        var ResultSet = DBConn.executeCachedQuery(SQLString);
    } catch (err) {
        logger.error("Error occurred while executing select statement " + SQLString + " At - " + DateUtil.getCurrentDate("yyyyMMddHHmmss"));
    } finally {
        if (DBConn) {
            CloseDBCon();
        }
    }
    return ResultSet;
}

/**
    executeInsertQuery

    @param {String} SQLString
    @param {String} DBConn
    @return ResultSet
*/
function executeInsertQuery(SQLString) { //Insert query execution for fetching the data from DB
	channelMap.put("SQLString1",SQLString)
    try {
        DBConn = OpenDBCon();
        var ResultSet = DBConn.executeUpdate(SQLString);
    } catch (err) {
        logger.error("Error occurred while executing select statement " + SQLString + " At - " + DateUtil.getCurrentDate("yyyyMMddHHmmss"));
    } finally {
        if (DBConn) {
            CloseDBCon();
        }
    }
    return ResultSet;
}

function executeScalar(sql) {
    if (sql && sql != '') {
        var result = null;
        var _conn = OpenDBCon();
        var response = _conn.executeCachedQuery(sql);
        if (response.next()) {
            result = '' + response.getString(1);
        }
        response.close();
        return result;
    }
}

//Function for Corey Patient ID Generation  with Prefix CPI*** -- Not being used
function generateCoreyPatientID() {
    SQLString = "SELECT TOP 1 [CoreyPatientID] AS CoreyPatientID FROM CMNPatientMapping ORDER BY [CoreyPatientID] DESC";
    var unique = executeScalar(SQLString);
    /*while (resultSet.next()) {
        var unique = resultSet.getString("CoreyPatientID");
        channelMap.put("uniqueCoreyPatientID", unique);
    }*/
    channelMap.put("uniqueCoreyPatientID", unique);
    var uID = unique.split("CPI");
    var nonZero = parseInt(uID[1]);
    var nonZero = nonZero + 1;
    var newUID = "CPI" + leftPad(nonZero, 7);
    return newUID;
}

