	var LISTOFMODIFIERS = globalChannelMap.get('LISTOFMODIFIERS');
    if (LISTOFMODIFIERS == null) {
        LISTOFMODIFIERS = JSON.parse(FileUtil.read("C:/MirthConfig/Mappings/RadiologyModifierMapping.json"));
        logger.debug('LISTOFMODIFIERS loaded successfully');
        globalChannelMap.put('LISTOFMODIFIERS', LISTOFMODIFIERS);
    }
    if (LISTOFMODIFIERS == null) {
        logger.error("LISTOFMODIFIERS is not available");
        return null;
    }		

    for (var reasonKey in LISTOFMODIFIERS) {
        var reason = LISTOFMODIFIERS[reasonKey];
        if (reason && reason.Modifier == vNAME) {
            return reason.IEN;
        }
    }
