function GetSymptomForVistA(strCoreyCode) {

	var symptomMap = getSymptomMap();
		for (var symptomKey in symptomMap) {
	      var symptom = symptomMap[symptomKey];
	      if (symptom && symptom.CoreyCode == strCoreyCode) {
	          return symptom.IEN;
	      }
	  }
	  return '';
}
function GetSymptomForCorey(description) {

	var symptomMap = getSymptomMap();
		for (var symptomKey in symptomMap) {
	      var symptom = symptomMap[symptomKey];
	      if (symptom && symptom.Symptom == description) {
	          return symptom.CoreyCode;
	      }
	  }
	  return '';
}

function getSymptomMap(){
	var symptomMap = globalChannelMap.get('symptomMap');
    if (symptomMap == null) {
        symptomMap = JSON.parse(FileUtil.read("C:/MirthConfig/Mappings/SymptomMapping.json"));
        logger.debug('symptomMap loaded successfully');
        globalChannelMap.put('symptomMap', symptomMap);
    }
    if (symptomMap == null) {
        logger.error("symptomMap is not available");
        return null;
    }	

	return symptomMap;
}

