/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/

function encryptAES(stringToBeEncrpted, key) {
	var object = Packages.com.vista.client.CMNAES;
	var encryptedSting = object.cmnEncrypt(stringToBeEncrpted,key);
	return encryptedSting;
}

function decryptAES(stringToBeDecrpted, key) {
	channelMap.put('stringToBeDecrpted', stringToBeDecrpted);
	channelMap.put('key', key);
	var object = Packages.com.vista.client.CMNAES;
	var decryptedSting = object.cmnDecrypt(stringToBeDecrpted, key);
	return decryptedSting;
}

/**
	Get Mapped allergy name and IEN.
*/
function GetAllergyForVistA(strCoreyCode) {

	var allergyMap = globalChannelMap.get('allergyMap');
	if(allergyMap == null){	
		allergyMap = JSON.parse(FileUtil.read("C:/MirthConfig/Mappings/AllergyToIEN.JSON"));
		logger.debug('Allergy Map loaded successfully');
		globalChannelMap.put('allergyMap', allergyMap);
	}	
	 if(allergyMap == null){
	 	logger.error("Allergy Mapping is not available");
	 }
	for (var allergyKey in allergyMap) {
	      var allergy = allergyMap[allergyKey];
	      if (allergy && allergy.num == strCoreyCode) {
	          return allergy;
	      }
	  }
	 // return null;
}

function GetAllergyForCorey(ien, file) {

	var allergyMap = globalChannelMap.get('allergyMap');
	if(allergyMap == null){	
		allergyMap = JSON.parse(FileUtil.read("C:/MirthConfig/Mappings/AllergyToIEN.JSON"));
		logger.debug('Allergy Map loaded successfully');
		globalChannelMap.put('allergyMap', allergyMap);
	}	
	 if(allergyMap == null){
	 	logger.error("Allergy Mapping is not available");
	 }
	for (var allergyKey in allergyMap) {
	      var allData=""
	      var allergy = allergyMap[allergyKey];
	      if (allergy && allergy.IEN == ien && allergy.File == file) {
	          // return allergy.CoreyCode;
	          //return allergy.num;
	          channelMap.put('allergydata',allergy.CoreyCode);
	           allData = allergy.CoreyCode;
	          return allData;
	      }else if (allergy && allergy.IEN == ien && file == "") {
	          // return allergy.CoreyCode;
	          //return allergy.num;
	          channelMap.put('allergydata',allergy.CoreyCode);
	           allData = allergy.CoreyCode;
	          return allData;
	      }
	  }
	  return "";
}

