        HOSPITALLOCATIONMAPPING = JSON.parse(FileUtil.read("C:/MirthConfig/Mappings/Hospital_LocationMapping.json"));
        logger.debug('HOSPITALLOCATION Mapping loaded successfully');
        globalChannelMap.put('HOSPITALLOCATIONMAPPING', APPLocationMap);
    }
