function GetAPPLocationForVistA(coreyCode) {
	var APPLocationMap = globalChannelMap.get('APPLocationMap');
    if (APPLocationMap == null) { 
        APPLocationMap = JSON.parse(FileUtil.read("C:/MirthConfig/Mappings/AppointmentLocation.json"));
        logger.debug('APPLocation Map loaded successfully');
        globalChannelMap.put('APPLocationMap', APPLocationMap);
    }
    if (APPLocationMap == null) {
        logger.error("APPLocation Mapping is not available");
        return null;
    }

    for (var reasonKey in APPLocationMap) {
        var reason = APPLocationMap[reasonKey];

        if (reason && parseInt(''+ reason.EHRFieldId) == parseInt(''+ coreyCode)) {
            return new String(reason.IEN);
        }
    }
    return ''; // Default Value
}

