   SQLString = "SELECT  Convert(Nvarchar(30),[Corey_ID])[Corey_ID] FROM [dbo].[CMNNConsultMapping] WHERE [Vista_id] = '" + vistACaseID + "'" + " AND Org_Name = '" + channelMap.get('orgname') + "'";
    channelMap.put("SELECTSQL", SQLString);
