function getCoreyPatientDataRecordID(sCoreyPtId, sVistAEntityId, sViewName) {
    var sCoreyEntityID = "0";
    var SQLString = "SELECT CoreyEntityID FROM CMN_PatientData_Mapping cpdm " +
        "JOIN CMNPatientMapping cpm on cpm.ID = cpdm.FK_PatientID " +
        "WHERE cpm.CoreyPatientID = '" + sCoreyPtId +
        "' AND cpm.Org_Name = '" + channelMap.get('orgname') +
        "' AND cpdm.ViewName = '" + sViewName +
        "' AND cpdm.VistAEntityId = '" + sVistAEntityId +
        "' AND cpdm.Org_Name = '" + channelMap.get('orgname') +
        "'";
    // channelMap.put("SELECTSQL", SQLString);
    var resultSet = executeSelectQuery(SQLString);
    channelMap.put(sViewName + ' ' + sCoreyPtId, resultSet);
    while (resultSet.next()) {
        sCoreyEntityID = resultSet.getString("CoreyEntityID");
    }
    return sCoreyEntityID;
}
function formatDate(date) {
	date = date.toString();
	if (date.length == 12){
		date = date.toString();

	} else {
		var dateStringFormat = "MMM dd,yyyy"
	     var outpattern = outpattern = "YYYYMMdd";
	     var d = DateUtil.convertDate(dateStringFormat, outpattern, date);
	     var date= d + '0000' ;
	}
	return date;
 }

function Getcdate(dateStr) {  //AUG 12, 2022@22:26 convert to YYYYMMDDHHMMSS
const dateObj = new Date(dateStr.replace('@', ' '));

const year = dateObj.getFullYear().toString().padStart(4, '0');
const month = (dateObj.getMonth() + 1).toString().padStart(2, '0');
const day = dateObj.getDate().toString().padStart(2, '0');
const hours = dateObj.getHours().toString().padStart(2, '0');
const minutes = dateObj.getMinutes().toString().padStart(2, '0');
const seconds = dateObj.getSeconds().toString().padStart(2, '0');

const formattedDate = year + month + day + hours + minutes + seconds;
return formattedDate;
}

