//"01/29/2023 09:20" and "01/29/2023" to the "YYYYMMDDHHMMSS" format
function formatDateLSF(dateString) {
  const dateParts = dateString.split(/[\s/:]/); // split into parts using regex
 const dateObj = new Date(dateParts[2] + '-' + dateParts[0] + '-' + dateParts[1] + 'T' + dateParts[3] + ':' + dateParts[4]);
   var formattedDate="";
  if (dateParts.length === 5) {
    formattedDate = dateObj.getFullYear().toString().padStart(4, '0') + (dateObj.getMonth()+1).toString().padStart(2, '0') + dateObj.getDate().toString().padStart(2, '0') + dateObj.getHours().toString().padStart(2, '0') + dateObj.getMinutes().toString().padStart(2, '0') + dateObj.getSeconds().toString().padStart(2, '0');
  } else {
    formattedDate = dateObj.getFullYear().toString().padStart(4, '0') + (dateObj.getMonth()+1).toString().padStart(2, '0') + dateObj.getDate().toString().padStart(2, '0') + '000000';
  }

  return formattedDate;
}

