function getOrganizationName(control, uselike) {
    if (uselike) {
        SQLString = "SELECT [Org_Name] FROM [dbo].[CMN_Corey_Org] WHERE [Message_Control_Id] LIKE '%" + control + "%';";
    } else {
        SQLString = "SELECT [Org_Name] FROM [dbo].[CMN_Corey_Org] WHERE [Message_Control_Id] = '" + control + "';";
    }

    channelMap.put("getOrganizationNameSQL", SQLString);
    var resultSet = executeSelectQuery(SQLString);
    while (resultSet.next()) {
        var Org_Name = resultSet.getString("Org_Name");
    }
    return Org_Name;
}

