function GetMedicationPriorityForCorey(vistaIEN) {
	 getmedprimap();
    for (var medicationKey in medicationPriorityMap) {
        var medication = medicationPriorityMap[medicationKey];
        if (medication && medication.IEN == vistaIEN) {
            return medication;
        }
    }
}

