	          return allergy;
	      }
	  }
	 // return null;
}

function GetAllergyForCorey(ien, file) {

	var allergyMap = globalChannelMap.get('allergyMap');
	if(allergyMap == null){	
		allergyMap = JSON.parse(FileUtil.read("C:/MirthConfig/Mappings/AllergyToIEN.JSON"));
		logger.debug('Allergy Map loaded successfully');
		globalChannelMap.put('allergyMap', allergyMap);
	}	
	 if(allergyMap == null){
	 	logger.error("Allergy Mapping is not available");
	 }
	for (var allergyKey in allergyMap) {
	      var allData=""
	      var allergy = allergyMap[allergyKey];
	      if (allergy && allergy.IEN == ien && allergy.File == file) {
	          // return allergy.CoreyCode;
	          //return allergy.num;
	          channelMap.put('allergydata',allergy.CoreyCode);
	           allData = allergy.CoreyCode;
	          return allData;
	      }else if (allergy && allergy.IEN == ien && file == "") {
	          // return allergy.CoreyCode;
	          //return allergy.num;
	          channelMap.put('allergydata',allergy.CoreyCode);
	           allData = allergy.CoreyCode;
	          return allData;
	      }
	  }
	  return "";
}

