function getCoreyPatientMedicationDataRecordID(sVistAEntityId) {
    SQLString = "SELECT [CoreyEntityID] FROM [dbo].[CMN_PatientData_MedicationMapping] WHERE [VistAEntityId] = '" + sVistAEntityId + "'" + " AND Org_Name = '" + channelMap.get('orgname') + "'";
    channelMap.put("SELECTSQL", SQLString);
    var resultSet = executeSelectQuery(SQLString);
    while (resultSet.next()) {
        var sCoreyEntityID = resultSet.getString("CoreyEntityID");
        channelMap.put("CoreyEntityID", sCoreyEntityID);
    }
    return sCoreyEntityID;
}

