function GetVistaConfiguration(orgName) {
    var map = globalMap.get('IntegrationConfig');

    for (var key in map.CoreyServers) {
        var masterItem = map.CoreyServers[key];
        if (masterItem.Org_Name.toUpperCase() == orgName.toUpperCase()) {
            //RefreshCoreyServerToken(masterItem);
            return masterItem;
        }
    }
}

function PutVistaCoreyConfigToGlobalM1ap() {
    // logger.debug('Inside PutVistaCoreyConfigToGlobalMap');
    var configFilePath = 'C:/MirthConfig/IntegrationConfig.json';
    var map = JSON.parse(ReadLocalFile(configFilePath));

    for (var key in map.CoreyServers) {
        var masterItem = map.CoreyServers[key];
        map.CoreyServers[key] = RefreshCoreyServerToken(masterItem, true);
    }
    globalMap.put('IntegrationConfig', map);
    globalMap.put('VistaMessageLogging', map.VistaServer.MessageLogging);
    globalMap.put('VistaWriteBack', map.VistaServer.WriteBack);
    return map;
}

function RefreshCoreyServerToken(orgObj, doNotUpdateMap) {
    // logger.debug('Inside RefreshCoreyServerToken for ' + orgObj.Org_Name);
	if(!doNotUpdateMap){
		doNotUpdateMap = false;
	}
    var now = DateUtil.getDate('yyyyMMddHHmmss', DateUtil.formatDate('yyyyMMddHHmmss', new java.util.Date())).getTime();

    // Check for time validity
    // if (orgObj && orgObj.token && orgObj.ExpirationSecond > now) return orgObj;

    var baseURLKey = orgObj.BaseURL.toUpperCase(); //+ "SERVERINTEGRATION_APPSTORE";
    // logger.debug('1. baseURLKey: ' + baseURLKey);
    var encrypted = orgObj.TokenRequest; // globalMap.get('VHACHY-CoreyTokenRequest');
    // logger.debug('2. encrypted: ' + encrypted);
    var object = Packages.com.vista.client.CMNVistAHttpClient;
    var url = baseURLKey + "RegisterDeviceid_s.aspx";
    // logger.debug('3. url: ' + url);
    // var auth = object.cmnExecutePost(url, encrypted, "", "");
    var auth = object.cmnExecutePost(url, encrypted, "");
    var json = object.cmnGetJSON(auth);   
    // logger.debug('3. json: ' + json);
    var decrypt = decryptAES(json, baseURLKey);
    var serverJson = new java.lang.String(decrypt, 'UTF-8');
    // logger.debug(serverJson);
    var parsedJSON = JSON.parse(serverJson);
    var token = parsedJSON['token'];

    orgObj.token = token;
    orgObj.ExpirationSecond = now + 3600;

    // logger.debug(orgObj.Org_Name + ' : ' + token);

    if (doNotUpdateMap && doNotUpdateMap == false) {
        var map = globalMap.get('IntegrationConfig'); // || PutVistaCoreyConfigToGlobalMap();
        for (var key in map.CoreyServers) {
            var masterItem = map.CoreyServers[key];
            if (masterItem.Org_Name == orgObj.Org_Name) {
                map.CoreyServers[key] = orgObj;
            }
        }
        // logger.debug('map after RefreshCoreyServerToken' + JSON.stringify(map));
        globalMap.put('IntegrationConfig', map);
    }
    return orgObj;
}

function generateAutotoken(orgObj) {
    // logger.debug('Inside RefreshCoreyServerToken for ' + orgObj.Org_Name);

    var now = DateUtil.getDate('yyyyMMddHHmmss', DateUtil.formatDate('yyyyMMddHHmmss', new java.util.Date())).getTime();

    // Check for time validity
    // if (orgObj && orgObj.token && orgObj.ExpirationSecond > now) return orgObj;

    var baseURLKey = orgObj.BaseURL.toUpperCase(); //+ "SERVERINTEGRATION_APPSTORE";
    // logger.debug('1. baseURLKey: ' + baseURLKey);
    var encrypted = orgObj.TokenRequest; // globalMap.get('VHACHY-CoreyTokenRequest');
    // logger.debug('2. encrypted: ' + encrypted);
    var object = Packages.com.vista.client.CMNVistAHttpClient;
    var url = baseURLKey + "RegisterDeviceid_s.aspx";
    // logger.debug('3. url: ' + url);
    // var auth = object.cmnExecutePost(url, encrypted, "", "");
    var auth = object.cmnExecutePost(url, encrypted, "");
    var json = object.cmnGetJSON(auth);   
    // logger.debug('3. json: ' + json);
    var decrypt = decryptAES(json, baseURLKey);
    var serverJson = new java.lang.String(decrypt, 'UTF-8');
    // logger.debug(serverJson);
    var parsedJSON = JSON.parse(serverJson);
    var token = parsedJSON['token'];

    orgObj.tokenMirth = token;
    orgObj.ExpirationSecond = now + 36000;

    // logger.debug(orgObj.Org_Name + ' : ' + token);

    return token;
}


function ReadLocalFile(filePath) {
    var json = FileUtil.read(filePath);
    return json;
}

function createAckForError(coreyId, error, action, patientId, orgname) {
	var respJson = {};
	respJson.ViewName = "VistaAckView";
	respJson.Action = action || "SETTINGS";
	respJson.SystemName = "Vista";
	respJson.PatientId = patientId || "";
	respJson.Id = coreyId;
	respJson.CreatedForUsers = [];
	respJson.IdentifierForUpdate = "";
	respJson.CreatedForUsers = [];
	respJson.ErrorCode = error.ErrorCode || "0";
	respJson.ErrorDescription = error.ErrorDescription || "";
	respJson.Data = [];
	channelMap.put('ErrorResponse', JSON.stringify(respJson));
	var orgObject = GetVistaConfiguration(orgname);
	sendCoreyResponse(orgObject, JSON.stringify(respJson));
}

function sendCoreyResponse(orgObject, jsonResponse) { // Sending Corey server response using token and API
	var coreySendCounter = 0;
	while (coreySendCounter < 5) {
		var ServerURL = orgObject.BaseURL + "RequestedDataFromMirth_s.aspx?token=" + orgObject.token;
		// channelMap.put('CoreyServerURL', ServerURL);
		var utilityObject = Packages.com.vista.client.CMNVistAHttpClient;
		var encrypt = encryptAES(jsonResponse, orgObject.token);
		var ServerResponse = utilityObject.cmnExecutePost(ServerURL, encrypt, "");
		var json = utilityObject.cmnGetJSON(ServerResponse);
		var decrypt = decryptAES(json, orgObject.token);
		var serverJson = new java.lang.String(decrypt, 'UTF-8');
		channelMap.put('CoreyServerResponse-Error', serverJson);
		var jsonObj = JSON.parse(serverJson);
		var code = jsonObj['Result']['Code'];
		if (code == 1000) { //if token is invalid, generate new token.
			RefreshCoreyServerToken(orgObject);
			coreySendCounter++;
			channelMap.put('coreySendCounter', coreySendCounter);
		} else {
			break;
		}
	}
}

function GetVitalsReadMap(QValue,vitaltype) {		
	var VITALSQUALIFIERMAPPING = globalChannelMap.get('VITALSQUALIFIERMAPPING');
    if (VITALSQUALIFIERMAPPING == null) {
        VITALSQUALIFIERMAPPING = JSON.parse(FileUtil.read("C:/MirthConfig/Mappings/VitalQualifierMapping.json"));
        logger.debug('VITALSQUALIFIERMAPPING loaded successfully');
        globalChannelMap.put('VITALSQUALIFIERMAPPING', VITALSQUALIFIERMAPPING);
    }
    if (VITALSQUALIFIERMAPPING == null) {
        logger.error("VITALSQUALIFIERMAPPING is not available");
        return null;
    }	
		for (var qualifierKey in VITALSQUALIFIERMAPPING) {
	      var qualifier = VITALSQUALIFIERMAPPING[qualifierKey];
	      if (qualifier && (qualifier.TYPE == vitaltype) && (qualifier.QUALIFIER == QValue) ) {
	          return qualifier.FID;
	      }
	     }
	     return ""
}

