function getPatientID(patientID, Message_Control_Id) {
    var coreyPatId = ""
    if (patientID) {
        var SQLString = "Select [CoreyPatientID] AS CoreyPatientID from CMNPatientMapping where [EXTSystemPatientID] = '" + patientID + "'" + " AND Org_Name = '" + channelMap.get('orgname') + "'"; // changed [VistaPatientID] to [EXTSystemPatientID] 
        channelMap.put("getPatientID", SQLString);
        coreyPatId = executeScalar(SQLString);
        channelMap.put("coreyPatId", coreyPatId);
    }
    /*while (resultSet.next()) {
        var coreyPatId = resultSet.getString("CoreyPatientID");
        channelMap.put("coreyPatId", coreyPatId);
    }*/
    if ((coreyPatId == null || typeof coreyPatId == 'undefined' || coreyPatId.trim().length <= 0) && Message_Control_Id) {
        var SQLString = "SELECT [Patient_Id] FROM CMN_Corey_Org WHERE [Message_Control_Id] = '" + Message_Control_Id + "'" + " AND Org_Name = '" + channelMap.get('orgname') + "'"; // changed [VistaPatientID] to [EXTSystemPatientID] 
        channelMap.put("getPatientID", SQLString);
        coreyPatId = executeScalar(SQLString);
        channelMap.put("coreyPatId", coreyPatId);
        /*while (resultSet.next()) {
            var coreyPatId = resultSet.getString("Patient_Id");
            channelMap.put("coreyPatId", coreyPatId);
        }*/
    }

    return coreyPatId;
}

