function GetHospitalLocation(timevalue) {
	var hLoc="";
     if (timevalue <=0800) {
		hLoc= "32"
     } else if (timevalue <=1600) {
		hLoc="33"
     } else {
		hLoc="34"
     }
     return hLoc;
}

